mapper.select:

WHERE estado='Disponible' 

  AND tipo = #{tipo}


  AND capacidad=#{capacidad}


  AND ubicacion=#{ubicacion}

SELECT id, nombre, ubicacion, tipo, capacidad, estado, inicio_disponibilidad, fin_disponibilidad, (SELECT COUNT(*) FROM reserva, usuario WHERE reserva.recurso=rec.id AND reserva.estado='Activa' AND reserva.usuario = usuario.correo
AND usuario.u_area = #{programa}
) AS numero FROM recurso as rec
rec.tipo=#{tipo}
ORDER BY
numero DESC,
(SELECT COUNT(*) FROM reserva as res, evento as eve, usuario WHERE res.recurso=rec.id AND res.estado='Activa' and eve.reserva=res.id AND eve.estado='Activa' AND usuario.correo=res.usuario
AND usuario.u_area = #{programa}
AND substring(to_char(#{franja.left}, 'YYYY-MM-DD HH24:MI:SS'), 12) <= substring(to_char(hora_inicio, 'YYYY-MM-DD HH24:MI:SS'), 12) AND substring(to_char(hora_fin, 'YYYY-MM-DD HH24:MI:SS'), 12) <= substring(to_char({franja.right}, 'YYYY-MM-DD HH24:MI:SS'), 12)
AND #{rango.left} <= eve.hora_inicio AND eve.hora_fin <= #{rango.right}
) DESC LIMIT 10;

SELECT id, nombre, ubicacion, tipo, capacidad, estado, inicio_disponibilidad, fin_disponibilidad, (SELECT COUNT(*) FROM reserva, usuario WHERE reserva.recurso=rec.id AND reserva.estado='Activa' AND reserva.usuario = usuario.correo
AND usuario.u_area = #{programa}
) AS numero FROM recurso as rec
rec.tipo=#{tipo}
ORDER BY
numero ASC,
(SELECT COUNT(*) FROM reserva as res, evento as eve, usuario WHERE res.recurso=rec.id AND res.estado='Activa' and eve.reserva=res.id AND eve.estado='Activa' AND usuario.correo=res.usuario
AND usuario.u_area = #{programa}
AND substring(to_char(#{franja.left}, 'YYYY-MM-DD HH24:MI:SS'), 12) <= substring(to_char(hora_inicio, 'YYYY-MM-DD HH24:MI:SS'), 12) AND substring(to_char(hora_fin, 'YYYY-MM-DD HH24:MI:SS'), 12) <= substring(to_char({franja.right}, 'YYYY-MM-DD HH24:MI:SS'), 12)
AND #{rango.left} <= eve.hora_inicio AND eve.hora_fin <= #{rango.right}
) ASC LIMIT 10;

SELECT k || ' - ' || t as rango, (SELECT COUNT(*) from evento as eve1 where t1.k <= substring(to_char(eve1.hora_inicio, 'YYYY-MM-DD HH24:MI:SS'), 12) and substring(to_char(eve1.hora_fin, 'YYYY-MM-DD HH24:MI:SS'), 12) <= t
AND #{tipo} = (SELECT recurso.tipo FROM reserva, recurso WHERE reserva.recurso=recurso.id AND eve1.reserva=reserva.id)
AND #{rango.left} <= eve1.fecha_inicio AND eve1.fecha_fin <= #{rango.right}
AND #{programa} = (SELECT usuario.u_area FROM reserva, usuario WHERE reserva.usuario=usuario.correo AND eve1.reserva=reserva.id)
) as numero FROM (SELECT DISTINCT ON(k,t ) SUBSTRING(to_char(eve.hora_inicio, 'YYYY-MM-DD HH24:MI:SS'), 12) as k, SUBSTRING(to_char(eve.hora_fin, 'YYYY-MM-DD HH24:MI:SS'), 12) as t FROM evento as eve
substring(to_char(#{franja.left}, 'YYYY-MM-DD HH24:MI:SS'), 12) <= substring(to_char(eve.hora_inicio, 'YYYY-MM-DD HH24:MI:SS'), 12) AND substring(to_char(#{eve.hora_fin}, 'YYYY-MM-DD HH24:MI:SS'), 12) <= substring(to_char(#{franja.right}, 'YYYY-MM-DD HH24:MI:SS'), 12)
GROUP BY hora_inicio, hora_fin ) as t1 ORDER BY numero DESC;

SELECT k || ' - ' || t as rango, (SELECT COUNT(*) from evento as eve1 where t1.k <= substring(to_char(eve1.hora_inicio, 'YYYY-MM-DD HH24:MI:SS'), 12) and substring(to_char(eve1.hora_fin, 'YYYY-MM-DD HH24:MI:SS'), 12) <= t
AND #{tipo} = (SELECT recurso.tipo FROM reserva, recurso WHERE reserva.recurso=recurso.id AND eve1.reserva=reserva.id)
AND #{rango.left} <= eve1.fecha_inicio AND eve1.fecha_fin <= #{rango.right}
AND #{programa} = (SELECT usuario.u_area FROM reserva, usuario WHERE reserva.usuario=usuario.correo AND eve1.reserva=reserva.id)
) as numero FROM (SELECT DISTINCT ON(k,t ) SUBSTRING(to_char(eve.hora_inicio, 'YYYY-MM-DD HH24:MI:SS'), 12) as k, SUBSTRING(to_char(eve.hora_fin, 'YYYY-MM-DD HH24:MI:SS'), 12) as t FROM evento as eve
substring(to_char(#{franja.left}, 'YYYY-MM-DD HH24:MI:SS'), 12) <= substring(to_char(eve.hora_inicio, 'YYYY-MM-DD HH24:MI:SS'), 12) AND substring(to_char(#{eve.hora_fin}, 'YYYY-MM-DD HH24:MI:SS'), 12) <= substring(to_char(#{franja.right}, 'YYYY-MM-DD HH24:MI:SS'), 12)
GROUP BY hora_inicio, hora_fin ) as t1 ORDER BY numero ASC;
