mapper.update:
update reserva as res
	set estado=#{estado}
WHERE
	res.id = #{reserva};

mapper.select:


	AND res.usuario = #{usuario} AND
	(SELECT MAX(eve1.hora_fin) FROM evento as eve1 WHERE eve1.reserva=res.id) < now()

ORDER BY (SELECT MIN(eve1.hora_inicio) FROM evento as eve1 WHERE eve1.reserva=res.id) DESC;


AND res.tipo!='Simple' AND res.estado='Activa'

	AND res.tipo = #{tipoReserva}


	AND usu.u_area = #{programa}


	AND rec.tipo = #{tipoRecurso}


	AND 
	NOT((#{rango.left} <= (SELECT MIN(eve1.hora_inicio) from evento as eve1 where eve1.reserva = res.id AND eve1.estado='Activa') AND 
		#{rango.right} <= (SELECT MIN(eve1.hora_inicio) from evento as eve1 where eve1.reserva = res.id AND eve1.estado='Activa')) OR 
		((SELECT MAX(eve1.hora_fin) from evento as eve1 where eve1.reserva = res.id AND eve1.estado='Activa') <=  #{rango.right}  AND
		(SELECT MAX(eve1.hora_fin) from evento as eve1 where eve1.reserva = res.id AND eve1.estado='Activa') <=  #{rango.left})
	)


	substring(to_char(#{eve.hora_fin}, 'YYYY-MM-DD HH24:MI:SS'), 12) <= #{franja.right}
	AND #{franja.left}  <= substring(to_char(#{eve.hora_inicio}, 'YYYY-MM-DD HH24:MI:SS'), 12)


AND res.tipo!='Simple' AND res.estado='Cancelada'

	AND res.tipo = #{tipoReserva}


