mapper.update:
update reserva as res
	set estado=#{estado}
WHERE
	res.id = #{reserva};

mapper.select:


	AND res.usuario = #{usuario} AND
	(SELECT MAX(eve1.hora_fin) FROM evento as eve1 WHERE eve1.reserva=res.id) < now()

ORDER BY (SELECT MIN(eve1.hora_inicio) FROM evento as eve1 WHERE eve1.reserva=res.id) DESC;
